salientDIF <- function(m1, s1, m2, s2, threshold = 0.33) {    # Calculate the mean of the difference D = x2 - x1   m_D <- m2 - m1      # Calculate the standard deviation of the difference D = x2 - x1   s_D <- abs(s2 - s1)      # Calculate the Z-scores for the positive and negative thresholds   Z_positive <- (threshold - m_D) / s_D   Z_negative <- (-threshold - m_D) / s_D      # Calculate the probability that D is outside the range (-threshold, threshold)   probability <- pnorm(Z_negative) + (1 - pnorm(Z_positive))      # Return the calculated probability   return(probability) }
